import React from 'react';
import ReactDOM from 'react-dom';

function App() {
  const [CashFlow, setCashflow] = React.useState(0);
  const [inflow, setInflow] = React.useState(0);
  const [outflow, setOutflow] = React.useState(0);
  const [balance, setBalance] = React.useState(0);

  const inputValue = (e) => {
    setCashflow(e.target.value);
  }
  const indflow = (e) => {
    setInflow(inflow + parseInt(CashFlow, 10));
    setBalance(inflow+outflow);
    setCashflow(0);
  }
  const outdflow = (e) => {
    setOutflow(outflow + parseInt(CashFlow, 10))
    setCashflow(0);
    setBalance(inflow+outflow);
  }
  const cashdflow = (e) => {
    if (CashFlow > 0) {
      console.log("positive");
      indflow();
    }
    else {
      console.log(outdflow());
      outdflow();
    }
  }
  return (<>
  Balance :SR {balance} <br/> 
    Total Inflow :SR {inflow} <br />
Total Outflow :SR {outflow} <br />
    <input type="text" placeholder="cash flow"
      onChange={inputValue}
      value={CashFlow}
    />
    <br />
    <button
      onClick={cashdflow}
    >
      Cashflows  </button><br />
  </>
  )
};
ReactDOM.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>,
  document.getElementById('root')
);
