import React from 'react';
import ReactDOM from 'react-dom';

function App () {

  const [cashflow, setCashflow] = React.useState(0);
  const [inflow, setInflow] = React.useState(0);
  const [outflow, setOutflow] = React.useState(0);
  const [inputValue, setInputValue] = React.useState(0);

  const indflow = (e) => {
  setInflow(inflow + cashflow);
}
// const cashflow = (e) => {
//   setCashflow(cashflow);
// }
const outdflow = (e) => {
  setOutflow(outflow + cashflow);
}
const cashdflow = (e) => {
  if(cashflow >= 0){
    console.log("positive");
    indflow; 
    }
    console.log("negative");
    outdflow;
};
return(
<>
Total Inflow :SR {inflow} <br />
Total Outflow :SR {outflow} <br />
<input type="number" placeholder="cash flow" 
   onChange={inputValue}   
      value={cashflow}/><br/>
<button onClick={cashdflow}> Cashflows  </button><br/>

</>
)
}


ReactDOM.render(
  <React.StrictMode>

 <App />>

  </React.StrictMode>,
  document.getElementById('root')
);
