import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

function App() {
  const [CashFlow, setCashflow] = React.useState();
  const [inflow, setInflow] = React.useState(0);
  const [outflow, setOutflow] = React.useState(0);
  const [nature, setNature] = React.useState("");
  const [trsflow, setTrsflow] = React.useState([{key:'', value:0}]);

  const inputTxt = (e) => {
    setNature(e.target.value);
  }
  setCashflow(e.target.value);
  const inputValue = (e) => {
  }
  const indflow = (e) => {
    setInflow(inflow + parseInt(CashFlow, 10));
    // setBalance(inflow+outflow);
    setCashflow(0);
  }
  const outdflow = (e) => {
    setOutflow(outflow + parseInt(CashFlow, 10));
    // setBalance(inflow+outflow);
    setCashflow(0);
  }
  const cashdflow = (e) => {
    e.preventDefault();
    setTrsflow(trsflow =>[...trsflow, (nature + " of SR " + CashFlow)]);
    // setTrsflow(trsflow.concat(nature + " of SR " + CashFlow));
    // setTrsflow(trsflow.concat(nature,CashFlow));
    if (CashFlow > 0) {
      indflow();
    }
    else {
      outdflow();
    }
  }
  return (<>
    <div className="main-container">

      <h1 className='header'>Cash Flow</h1>
      <h3>Balance :SR {inflow + outflow}</h3> <br />
      <div className='in-ex-container'>
        <h4>Total Inflow <br /><div className="inflow">
          SR {inflow}
        </div>
        </h4>
        <h4>Total Outflow <br />
          <div className="outflow">
            SR {outflow}
          </div>
        </h4>
        <br />
      </div>
      <h2>Transaction History</h2><hr /><br />
      <ul className="transaction-list">
        {trsflow.map((transaction,ind) => {
          return (
            <li key={ind}>
              <span>{transaction}</span>
              {/* <span>{transaction.description}</span>
              <span>{transaction.amount}</span> */}
            </li>)
        })}
      </ul>
      <br />
      <h2>Add New Transaction</h2>
      <form onSubmit={cashdflow} className="transaction-form">
        <label>Amount </label>
        <input
          type="number"
          placeholder="cash flow"
          onChange={inputValue}
          value={CashFlow}
          required
        /> <br />
        <label>Nature of transaction </label>
        <input
          type="text"
          placeholder="Natrue of transaction"
          onChange={inputTxt}
          value={nature}
          required
        /> <br />
        <button
          className="btn"
          type="submit" >
          Add Transaction  </button><br />
      </form>
    </div>
  </>
  )
};
ReactDOM.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>,
  document.getElementById('root')
);
