import React from 'react';
import ReactDOM from 'react-dom';

function App () {
  const [CashFlow, setCashflow] = React.useState(0);
  // const [cashdflow, setCashdflow] = React.useState(0);
  const [inflow, setInflow] = React.useState(0);
  const [outflow, setOutflow] = React.useState(0);
  // const [inputValue, setInputValue] = React.useState(0);

  const indflow = (e) => {
  setInflow(inflow + CashFlow);
}
const inputValue = (e) => {
  setCashflow(e.target.value);
};
const outdflow = (e) => {
  setOutflow(outflow + CashFlow);
}
const cashdflow = (e) => {
  if(CashFlow > 0){
    console.log("positive");
   indflow(); 
    }
    else{console.log(outdflow());
    outdflow();
}}
return(<>
Total Inflow :SR {inflow} <br />
Total Outflow :SR {outflow} <br />
<input type="text" placeholder="cash flow" 
   onChange={inputValue}   
      value={CashFlow}
      />
<br/>
<button 
onClick={cashdflow}
> 
Cashflows  </button><br/>
</>
)
};


ReactDOM.render(
  <React.StrictMode>

 <App />

  </React.StrictMode>,
  document.getElementById('root')
);
