import React , {useState} from "react";
import ReactDOM from "react-dom";
import "./styles.css";

function Room() {
  const [isLit, setLit] = React.useState(false);
  const [temp, setTemp] = React.useState(6);
  const [count1, setCount] = React.useState(10);

  return (
    <div className={`room ${isLit ? "lit1" : "dark1"}`}>

      the room is {isLit ? "lit" : "dark"} <br />
      <button onClick={() => setLit(!isLit)}> Flip the light </button> <br />
      <button onClick={() => setLit(true)}> light On </button>
      <button onClick={() => setLit(false)}> Light Off </button> <br />
      The current temprature is {temp} 
      <button onClick={() => setTemp(temp + 1)}> + </button>
      <button onClick={() => setTemp(temp - 1)}> - </button> <br />
      Counter {count1}
      <button OnClick={() => setCount(count1 + 11)}>Increase</button>
      <button OnClick={() => setCount(count1 - 1)}>Decrease</button>

    </div>
  );
}

function OneTimeButton(props) {
  const [clicked, setClicked] = useState(false);

  function doClick() {
    // props.onClick();
    setClicked(true);
  }
  return (
    <button
      onClick={clicked ? undefined : doClick}
      disabled={clicked}
    >
      You Can Only Click Me Once
    </button>
  );
}
ReactDOM.render(<div> <Room /> <OneTimeButton /> </div>, document.getElementById("root"));
